// Shared type definitions for Arka Inspection

export type QuestionType = 'text' | 'number' | 'photo' | 'select' | 'checkbox' | 'date';

export interface Question {
  id: string;
  text: string;
  type: QuestionType;
  mandatory?: boolean;
  allowAttachment?: boolean;
  options?: string[];
}

export interface Section {
  id: string;
  title: string;
  questions: Question[];
}

export interface Template {
  id: string;
  title: string;
  description?: string;
  createdBy?: string;
  createdAt?: string;
  sections: Section[];
}

export interface Answer {
  questionId: string;
  value?: string | number | string[];
  attachments?: Attachment[];
}

export interface Attachment {
  id: string;
  url: string;
  mimeType?: string;
  uploadedAt?: string;
}

export interface Inspection {
  id: string;
  vesselId: string;
  inspectorId: string;
  templateId: string;
  date: string; // ISO date
  status: 'draft' | 'in_progress' | 'completed';
  answers: Answer[];
}

export interface Vessel {
  id: string;
  name: string;
  type: string;
  registrationNumber: string;
}

export interface Inspector {
  id: string;
  name: string;
  email: string;
  role: 'admin' | 'inspector';
}
